// Seems to me there is.


// TODO check and tune performance once the naive implementation works: https://keebsforall.com/blogs/mechanical-keyboards-101/reduce-keyboard-input-lag-with-qmk

static void render_status(void) {
    // Write layer
    oled_write("Layer: ", false);
    switch (get_highest_layer(layer_state)) {
        case BASE_LAYER:
            oled_write_ln("Base", false);
            break;
        case UPPER_LAYER:
            oled_write_ln("Upper", false);
            break;
        case LOWER_LAYER:
            oled_write_ln("Lower", false);
            break;
        case CONTROL_LAYER:
            oled_write_ln("Control", false);
            break;
        default:
            oled_write_ln("Undefined", false);
            break;
    }

    // Write autocorrect state
    oled_write("Autocorrect: ", false);
    oled_write_ln(autocorrect_is_enabled() ? "On" : "Off", false);

    oled_advance_page(true);

    // Write autocorrect notification
    if (ac_show_noti) {
        if (timer_elapsed32(ac_noti_timer) < AC_NOTI_LINGER_TIME_MS) {
            oled_write_ln(ac_last_typo, false);
            oled_write_ln(centered_down_arrow_line, false);
            oled_write_ln(ac_last_fix, false);
        } else {
            oled_write_ln(empty_line, false);
            oled_write_ln(empty_line, false);
            oled_write_ln(empty_line, false);
            ac_show_noti = false;
        }
    }
}


static void render_bongo_cat(void) {
    int wpm = get_current_wpm();

    // Render bongo cat animation in upper half
    if (wpm < BONGO_IDLE_WPM_CUTOFF) {
        // Do idle animation, or skip if frame time not yet reached
        if (timer_elapsed32(idle_frame_timer) > BONGO_IDLE_FRAME_TIME_MS) {
            oled_write_raw(bongo_idle_frames[last_idle_frame_idx++ % BONGO_IDLE_FRAME_COUNT], ANIM_SIZE);
            idle_frame_timer = timer_read32();  // Reset timer
        }
    } else {
        // Do tapping animation, or skip if state still OK
        if (active_frame_idx != next_active_frame_idx) {
            oled_write_raw(bongo_active_frames[next_active_frame_idx], ANIM_SIZE);
            active_frame_idx = next_active_frame_idx;
        }
    }

    // Render WPM top left
    oled_set_cursor(0, 0);
    if (wpm < BONGO_IDLE_WPM_CUTOFF) {
        oled_write("   ", false);  // hide if 0
    } else {
        char wpm_buf[4];
        snprintf(wpm_buf, 4, "%-3d", wpm);
        oled_write(wpm_buf, false);
    }
}


oled_rotation_t oled_init_user(oled_rotation_t rotation) {
    return OLED_ROTATION_180;
}


bool oled_task_user(void) {
    if (is_keyboard_left()) {
        render_status();
        // render_bongo_cat();
    } else {
        render_bongo_cat();
        // render_status();
    }
    return false;
}

bool apply_autocorrect(uint8_t backspaces, const char *str, char *typo, char *correct) {
    // Save typo
    int i = 0;
    char *p = ac_last_typo;
    while (i < sizeof(ac_last_typo) && (*p++ = *typo++) != '\0')
        ;
    *p = '\0';

    // Save correction
    i = 0;
    p = ac_last_fix;
    while (i < sizeof(ac_last_fix) && (*p++ = *correct++) != '\0')
        ;
    *p = '\0';

    ac_show_noti = true;
    ac_noti_timer = timer_read32();

    return true;
}
